import React, { useEffect, useState } from "react";

const TypingAnimation = ({
  strings,
  typeSpeed = 150,
  delayAfterCompletion = 1000,
}) => {
  const [text, setText] = useState("");
  const [arrayIndex, setArrayIndex] = useState(0);
  const [charIndex, setCharIndex] = useState(0);
  const [cursorVisible, setCursorVisible] = useState(true);

  useEffect(() => {
    const cursorInterval = setInterval(() => {
      setCursorVisible((v) => !v);
    }, 500);
    return () => clearInterval(cursorInterval);
  }, []);

  useEffect(() => {
    if (!strings || strings.length === 0) {
      return; // If strings array is empty or undefined, exit early.
    }

    if (arrayIndex >= strings.length) {
      setArrayIndex(0);
      return;
    }

    if (charIndex === strings[arrayIndex].length) {
      setTimeout(() => {
        setText("");
        setCharIndex(0);
        setArrayIndex((prev) => (prev + 1) % strings.length);
      }, delayAfterCompletion);
      return;
    }

    const timerId = setTimeout(() => {
      setText((prev) => prev + strings[arrayIndex].charAt(charIndex));
      setCharIndex((prev) => prev + 1);
    }, typeSpeed);

    return () => clearTimeout(timerId);
  }, [charIndex, arrayIndex, strings, typeSpeed, delayAfterCompletion]);

  return (
    <span className="typing">
      {text}
      <span
        className="typing-cursor"
        style={{ opacity: cursorVisible ? 1 : 0 }}
      >
        |
      </span>
    </span>
  );
};

export default TypingAnimation;
