import React, { useState, useEffect } from "react";
import { useInView } from "react-intersection-observer";

function ArticlesSection() {
  const [articles, setArticles] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch("http://localhost:8000/research_papers/api/deep_learning_papers/")
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        setArticles(data);
        setIsLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
        setError(error);
        setIsLoading(false);
      });
  }, []);

  const [visibleCards, setVisibleCards] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      const scrollTop = window.scrollY;
      const windowHeight = window.innerHeight;

      // Calculate the number of cards to make visible based on scroll position
      const cardsToShow = Math.min(
        Math.ceil((scrollTop + windowHeight) / 300),
        articles.length
      );

      setVisibleCards(cardsToShow);
    };

    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, [articles]);

  if (isLoading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error.message}</div>;
  }

  return (
    <div
      className="flex flex-wrap relative items-center justify-center w-[100%] py-[8%]"
      id="articles"
    >
      {articles.map((article, index) => {
        const [ref, inView] = useInView({ triggerOnce: true, threshold: 0.1 });

        return (
          <div
            ref={ref}
            key={index}
            className={`flex flex-wrap lg:w-[30%] p-6 xl:w-[30%] sm:w-full w-full ${
              inView ? "animate-slideUp" : ""
            }`}
          >
            {/* Card content */}
          </div>
        );
      })}
    </div>
  );
}

export default ArticlesSection;
