import React, { useEffect, useState } from "react";

const OneWayTypingAnimation = ({ text = "", typeSpeed = 150, onComplete }) => {
  const [displayedText, setDisplayedText] = useState("");
  const [charIndex, setCharIndex] = useState(0);
  const [cursorVisible, setCursorVisible] = useState(true);

  const stringText = typeof text === "string" ? text : String(text);

  useEffect(() => {
    const cursorInterval = setInterval(() => {
      setCursorVisible((v) => !v);
    }, 500);
    return () => clearInterval(cursorInterval);
  }, []);

  useEffect(() => {
    const typeCharacter = () => {
      if (!isDeleting) {
        setText((prev) => prev + strings[arrayIndex].charAt(charIndex));
        setCharIndex((prev) => prev + 1);
      }
    };

    if (arrayIndex >= strings.length) {
      setArrayIndex(0);
      return;
    }

    if (!stringText || charIndex >= stringText.length) {
      onComplete?.();
      return;
    }

    const timerId = setTimeout(() => {
      setCursorVisible(true);
      typeCharacter();
    }, typeSpeed);

    return () => clearTimeout(timerId);
  }, [charIndex, stringText, text, typeSpeed, onComplete]);

  console.log("Typing: ", displayedText); // Debugging log

  return (
    <span className="one-way-typing">
      {displayedText}
      <span
        className="typing-cursor"
        style={{ opacity: cursorVisible ? 1 : 0 }}
      >
        |
      </span>
    </span>
  );
};

export default OneWayTypingAnimation;
