import React, { useEffect, useState } from "react";

const OneWayTypingAnimation = ({ text = "", typeSpeed = 25, onComplete }) => {
  const [displayedText, setDisplayedText] = useState("");
  const [charIndex, setCharIndex] = useState(0);
  const [cursorVisible, setCursorVisible] = useState(true);

  useEffect(() => {
    const cursorInterval = setInterval(() => {
      setCursorVisible((v) => !v);
    }, 500);

    return () => clearInterval(cursorInterval);
  }, []);

  useEffect(() => {
    if (!text) return; // Early return if text is not defined or empty

    if (charIndex >= text.length) {
      onComplete?.(); // Call onComplete callback if provided
      return;
    }

    const timerId = setTimeout(() => {
      setDisplayedText(text.substring(0, charIndex + 1));
      setCharIndex(charIndex + 1);
    }, typeSpeed);

    return () => clearTimeout(timerId);
  }, [charIndex, text, typeSpeed, onComplete]);

  return (
    <span className="one-way-typing">
      {displayedText}
      <span
        className="typing-cursor"
        style={{ opacity: cursorVisible ? 1 : 0 }}
      >
        |
      </span>
    </span>
  );
};

export default OneWayTypingAnimation;
